import React from 'react';
import validator from "email-validator";

import ErorMsg from '../Components/ErrorMsg';

class SignUpPage extends React.Component {



    constructor(props) {
        super(props);


        this.state = {
            firstName : '',
            lastName : '',
             email: '',
            password: '',
            submitted: false,
            loading: false,
            errorPassword : false,
            error: ''
        };


    }


    //____________to Handlle Input__________//

    handleChange =(e) => {
        const { name, value } = e.target;
        this.setState({ [name]: value });
    }

    handleSubmit =(e) => {
        e.preventDefault();

        //alert("clciked")

        this.setState({ submitted: true });
        const { email, password, firstName , lastName } = this.state;

       if(password.length <6)
       {
           this.setState({errorPassword:true});

       }

   if(email && password && firstname && lastname)
   {
   fetch('http://localhost:3001/user/user/subscribe',
   {
  method: "post",
  headers: {
    'Accept': 'application/json',
    'Content-Type': 'application/json'
  } , 
    body: JSON.stringify({
    'firstname': firstname,
    'lastname' : lastname,
    'email' : email ,
    'password' : password
  })

   })
      .then(response => response.json())
      .then(data => 
      {  

          console.log('data from server' 
          + JSON.stringify(data));
          alert("sucess user inscription")


      })


      .catch(err=> {
          console.log(err);
          alert("error");
      })


   }


    }

    render() {
        const { firstName , lastName ,email, password, submitted,
        errorPassword, loading, error } = this.state;
        return (
            <div className="col-md-6 col-md-offset-3">

                <h2>Sign Up</h2>
                <form name="form" onSubmit={this.handleSubmit}>


                <div className={'form-group' + (submitted && !password ? ' has-error' : '')}>
                        <label htmlFor="firstName">First Name</label>
                        <input type="text" className="form-control" name="firstName" value={firstName } onChange={this.handleChange} />
                        {submitted && !firstName  &&
                            <div className="help-block">First Name is required</div>
                        }
                    </div>




                    <div className={'form-group' + (submitted && !password ? ' has-error' : '')}>
                        <label htmlFor="lastName">Password</label>
                        <input type="text" className="form-control" name="lastName" value={lastName} onChange={this.handleChange} />
                        {submitted && !lastName &&
                            <div className="help-block">lastName is required</div>
                        }
                    </div>









                    <div className={'form-group' + (submitted && !email ? ' has-error' : '')}>
                        <label htmlFor="username">Email</label>
                        <input type="text" className="form-control" name="email" value={email} onChange={this.handleChange} />
                        {submitted && !email &&
                            /*<div className="help-block">Email is required</div>*/
                            <ErorMsg  msg="Email is required" />
                        }

                      {submitted && !email &&
                            /*<div className="help-block">Email is required</div>*/
                            <ErorMsg  msg="Email is required" />
                        }
                    </div>
                    <div className={'form-group' + (submitted && !password ? ' has-error' : '')}>
                        <label htmlFor="password">Password</label>
                        <input type="password" className="form-control" name="password" value={password} onChange={this.handleChange} />
                        {submitted && !password &&
                            <div className="help-block">Password is required</div>
                        }

                          {submitted && errorPassword &&
                             <ErorMsg  msg="Your Password is too short" />
                        }
                    </div>
                    <div className="form-group">
                        <button className="btn btn-primary" disabled={loading} type="submit">Login</button>
                        {loading &&
                            <img src="data:image/gif;base64," />
                        }
                    </div>
                    {error &&
                        <div className={'alert alert-danger'}>{error}</div>
                    }
                </form>
            </div>
        );
    }
}

export  default SignUpPage ;
