import React from 'react';
import validator from "email-validator";

import ErorMsg from '../Components/ErrorMsg';

class SignInPage extends React.Component {



    constructor(props) {
        super(props);


        this.state = {
            email: '',
            password: '',
            submitted: false,
            loading: false,
            error: ''
        };


    }


    //____________to Handlle Input__________//

    handleChange =(e) => {
        const { name, value } = e.target;
        this.setState({ [name]: value });
    }

    handleSubmit =(e) => {
        e.preventDefault();

        //alert("clciked")

        this.setState({ submitted: true });
        const { email, password, returnUrl } = this.state;
        validator.validate(email);

        // stop here if form is invalid
        /*if (!(email && password)) {
            return;
        }*/

        //this.setState({ loading: true });
        /*userService.login(username, password)
            .then(
                user => {
                    const { from } = this.props.location.state || { from: { pathname: "/" } };
                    this.props.history.push(from);
                },
                error => this.setState({ error, loading: false })
            );*/
    }

    render() {
        const { email, password, submitted, loading, error } = this.state;
        return (
            <div className="col-md-6 col-md-offset-3">

                <h2>Sign In</h2>
                <form name="form" onSubmit={this.handleSubmit}>
                    <div className={'form-group' + (submitted && !email ? ' has-error' : '')}>
                        <label htmlFor="username">Email</label>
                        <input type="text" className="form-control" name="email" value={email} onChange={this.handleChange} />
                        {submitted && !email &&
                            /*<div className="help-block">Email is required</div>*/
                            <ErorMsg  msg="Email is required" />
                        }

                      {submitted && !email &&
                            /*<div className="help-block">Email is required</div>*/
                            <ErorMsg  msg="Email is required" />
                        }
                    </div>
                    <div className={'form-group' + (submitted && !password ? ' has-error' : '')}>
                        <label htmlFor="password">Password</label>
                        <input type="password" className="form-control" name="password" value={password} onChange={this.handleChange} />
                        {submitted && !password &&
                            <div className="help-block">Password is required</div>
                        }
                    </div>
                    <div className="form-group">
                        <button className="btn btn-primary" disabled={loading} type="submit">Login</button>
                        {loading &&
                            <img src="data:image/gif;base64," />
                        }
                    </div>
                    {error &&
                        <div className={'alert alert-danger'}>{error}</div>
                    }
                </form>
            </div>
        );
    }
}

export  default SignInPage ;
